def get_surround(board, x, y):
  return [
    *board[y-1][x-1:x+1],
    board[y][x-1:x+2:2],
    *board[y+1][x-1:x+1]
  ]

if board[y][x] == white && get_surround(board, x, y).count(black) == 3:
  next_board[y][x] = black
elif board[y][x] == black && 2 <= get_surround(board, x, y).count(black) <= 3:
  next_board[y][x] = black
else:
  next_board[y][x] = white
